import axios from 'axios';

const API_URL = 'http://localhost:8080/api/auth/';

// Create an axios instance with default config
const axiosInstance = axios.create({
  baseURL: API_URL,
  headers: {
    'Content-Type': 'application/json'
  },
  withCredentials: true // This is important for handling cookies/sessions
});

class AuthService {
  login(username, password) {
    return axiosInstance
      .post('signin', { username, password })
      .then(response => {
        if (response.data.accessToken) {
          localStorage.setItem('user', JSON.stringify(response.data));
        }
        return response.data;
      });
  }

  logout() {
    localStorage.removeItem('user');
  }

  register(username, email, password, firstName, lastName, phoneNumber, roles) {
    return axiosInstance.post('signup', {
      username,
      email,
      password,
      firstName,
      lastName,
      phoneNumber,
      roles
    });
  }

  getCurrentUser() {
    try {
      return JSON.parse(localStorage.getItem('user'));
    } catch (error) {
      this.logout();
      return null;
    }
  }

  isUserLoggedIn() {
    const user = this.getCurrentUser();
    return !!user && !!user.accessToken;
  }

  getToken() {
    const user = this.getCurrentUser();
    return user?.accessToken;
  }

  hasRole(requiredRoles) {
    const user = this.getCurrentUser();
    if (!user) return false;
    
    return requiredRoles.some(role => user.roles.includes(role));
  }

  updateProfile(firstName, lastName, phoneNumber, currentPassword, newPassword) {
    return axiosInstance.put('profile', {
      firstName,
      lastName,
      phoneNumber,
      password: newPassword,
      currentPassword
    })
    .then(response => {
      if (response.data.accessToken) {
        localStorage.setItem('user', JSON.stringify(response.data));
      }
      return response.data;
    });
  }
}

// Add a request interceptor to include the auth token
axiosInstance.interceptors.request.use(
  (config) => {
    const token = AuthService.prototype.getToken();
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

export default new AuthService();
